import { useState } from 'react';

const statuses = ['All', 'In Progress', 'Complete'];

function List() {
    const [status, setStatus] = useState('all');

    function statusEntryClassNames(entry) {
        return "status__entry " + 
            ((status === entry.toLowerCase()) && "status__entry--selected");
    }

    function handleStatusEntryClick(e) {
        setStatus()
    }

    return (
        <main>
            <ul className='status'>
                {statuses.map((s, i) => 
                    <li 
                        className={statusEntryClassNames(s)} 
                        key={i}
                        onClick={e => handleStatusEntryClick(e)}>
                        {s}
                    </li>)
                }
            </ul>
            <ul className='tasks'>
                <li className='tasks__entry'>Do the loundary</li>
            </ul>
        </main>
    );
}

export default List;
