import { useState } from 'react';

const statuses = ['All', 'In Progress', 'Complete'];

function List() {
    const [status, setStatus] = useState('all');

    function statusEntryClassNames(entry) { 
        const names =  "status__entry " + (status.toLowerCase() === entry) ? "status__entry--selected" : ""; 
        console.log(names);
        return names;
    }

    return (
        <main>
            <ul className='status'>
                {statuses.map((s, i) => 
                    <li className={statusEntryClassNames(s)} key={i}>{s}</li>)
                }
            </ul>
            <ul className='tasks'>
                <li className='tasks__entry'>Do the loundary</li>
            </ul>
        </main>
    );
}

export default List;
