import right from './right.png';

function getRemainingTime(start, end) {
    try {
        let timeDiff = end.getTime() - start.getTime();
        const oneDay = 1000 * 60 * 60 * 24;
        let days = Math.floor(timeDiff / oneDay);
        // let years = Math.floor(days / 365.25);
        // days -= years * 365.25;
        // let months = Math.floor(days / 30.44);
        // days -= months * 30.44;

        let years = 0;
        let months = 0;
        days = Math.round(days);

        return {
            years: years,
            months: months,
            days: days
        };
    } catch (e) {
        return {
            years: 0,
            months: 0,
            days: 0
        }; 
    }
}

function Task({ task, onTaskClick }) {

    function formatDate(date) {
        console.log()
        const remains = getRemainingTime(new Date(), date);
        return `${remains.years} years, ${remains.months} months, ${remains.days} days`;
    }

    function formatHour(date) {
        const hours = date.getHours();
        const minutes = date.getMinutes();
        return (String(hours).length > 1 ? hours : '0' + hours) + ":" +
             (String(minutes).length > 1 ? minutes : '0' + minutes);
    }

    return (
        <div>
            <article className='tasks__task'>
                <div className='task__left'>
                    <div className='task__title'>{task.description}</div>
                    <div className='task__due'>
                        {
                            formatDate(task.due)
                        }
                    </div>
                </div>
                <div className='task__right'>
                    <div>
                        {formatHour(task.due)}
                    </div>
                    {task.status !== 'complete' && 
                        <img onClick={e => onTaskClick(e, task.id)} 
                            className='task__push' width='15px' src={right} alt='right'></img>}
                </div>
            </article>
        </div>
    );
}

export default Task;
